var assert = require('assert'),
    gen = require('./yield');

describe('Harmony', function () {
    it('Should be able to call next and get classes with appropriate values', function () {
        var c1, c2;
        c1 = gen.next();
        c2 = gen.next();
        assert.equal(c1.value.number, 1);
        assert.equal(c2.value.number, 2);
        assert.notDeepEqual(c1, c2);
        console.log(c1);
    });
});
